mapper.select:
SELECT nome, descricao, data, horario, local, preco
FROM tournaments
WHERE estado_id = #{estadoId} AND cidade_id = #{cidadeId}
AND data >= CURRENT_DATE
ORDER BY data ASC
LIMIT #{limit}

SELECT nome, descricao, data, horario, local, preco
FROM torneios
WHERE 1=1
 AND estado_id = #{estadoId} 
 AND cidade_id = #{cidadeId} 
 AND data = #{data} 
ORDER BY data ASC
